export interface ObserverCallback {
    (changeType: 'add' | 'remove', node: Node): void;
}
export default class Observer {
    static server: MutationObserver;
    static listeners: {
        node: Node;
        callback: ObserverCallback;
    }[];
    private static start;
    private static getNodesInDeep;
    private static getChildrenInDeep;
    static close(): void;
    static addListener(node: Node, callback: ObserverCallback): void;
}
